void map_load(Game* game, int map[18][80], int map_cur) {
    for (int y = 0; y < 18; y++) {
        for (int x = 0; x < 80; x++) {
            game->map[y][x] = map[y][x];
            if (game->map[y][x] == 100) {
                game->player.x = x;
                game->player.y = y;
            }
        }
    }

	game->map_cur = map_cur;
}

void map_change(Game* game) {
	if (game->map_cur == 0)
		map_load(game, level_2, 1);
	else if (game->map_cur == 1)
		map_load(game, level_3, 2);
	else if (game->map_cur == 2)
		map_load(game, level_4, 3);
	else if (game->map_cur == 3)
		map_load(game, level_5, 4);
}

