void map_draw(int map[18][80]) {
	// must change, its kinda slow
    for (int y = 0; y < 18; y++) {
        for (int x = 0; x < 80; x++) {
            char *prnt = " ";

            switch (map[y][x]) {
                case 1:
                    prnt = "\x1b[0;32mA\x1b[0m";
                    break;
                case 2:
                    prnt = "\x1b[0;34mW\x1b[0m";
                    break;
                case 3:
                    prnt = "\x1b[0;33m#\x1b[0m";
                    break;
                case 99:
                    prnt = "\x1b[1;30;0mO\x1b[0m";
                    break;
                case 100:
                    prnt = "\x1b[1;36m@\x1b[0m";
                    break;
            }

			fputs(prnt, stdout);
        }
		puts("");
    }
}